import React, { memo, useCallback } from 'react'
import PropTypes from 'prop-types'
import { useDispatch } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'

const SearchBar = ({ books, query }) => {
  const dispatch = useDispatch()

	// 	const mappedBooks = !(books == null || undefined) ? books.map((book, index) => {
	// 		console.log(book)
	// }) : <h4>No results</h4>

	const handleSubmit = useCallback((e, query) => {
		e.preventDefault()
		console.log('enter worked !')
    dispatch(getBooks(e.target.value))
		(books == null || undefined) ? books.map((book, index) => {
						console.log(book)
			}) : <h4>No results</h4>
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
      <form
				className="formSearchBar"
				onSubmit={handleSubmit}
			>
        <input
					className="searchBar"
       		type='text'
        	id='search'
        	value={query}
        	placeholder={'Search'}
        />
        <button
        	className='search-button'
				>
          <img
            srcSet={searchImage}
            className='search-image' alt=''></img>
        </button>
      </form>
    )
}

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}

export default memo(SearchBar);
