import React, { memo, useCallback } from 'react'
import PropTypes from 'prop-types'
import { useDispatch } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'

const SearchBar = ({ books, query }) => {
  const dispatch = useDispatch()

	// 	const mappedBooks = !(books == null || undefined) ? books.map((book, index) => {
	// 		console.log(book)
	// }) : <h4>No results</h4>

  const getBooksByQuery = useCallback((e) => {
      dispatch(getBooks(e.target.value))
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

	const EnterSubmit = (e, query) => {
		// if (e.key === 'Enter' && query == null) {
		// 	handleChange()
		e.preventDefault()
		console.log('enter worked !')
		handleGetBooks()
		// }
	}

  return (
      <form
				className="formSearchBar"
				onSubmit={EnterSubmit}
			>
        <input
					className="searchBar"
       		type='text'
        	id='search'
        	value={query}
        	placeholder={'Search'}
        />
        <button
        	className='search-button'
        	onClick={EnterSubmit}>
          <img
            srcSet={searchImage}
            className='search-image' alt=''></img>
        </button>
      </form>
    )
}

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}

export default memo(SearchBar);
