import React from 'react';
import { useDispatch } from 'react-redux'
import { setBooks, getBooks } from './actions/Actions';
import './App.css'
import SearchBar from './Components/searchBlock/SearchBar'


function App() {
  const mainHeader = () => {
    return (
      <div className="main-image">
        {/* overlay */}
        <h1>Search for Books</h1>
        <div className="filter">
          <SearchBar />
        </div>
      </div>
    )
  }

  const dispatch = useDispatch()

  const handleClick = () => {
    dispatch(setBooks(getBooks()))
  }

  return (
    <div className="App" onClick={handleClick}>
      {mainHeader()}
    </div>
  );
}

export default App;
