import React, { useMemo, useCallback, useState } from 'react';
import { useDispatch } from 'react-redux'
import { setBooks } from './actions/actions';
import './App.css'
import SearchBar from './Components/searchBlock/SearchBar.jsx'

const SearchComponent = () => {
  const [count, setCount] = useState(0);

  return (
    <div className="main-image" onClick={() => setCount(prev => prevprev + 1)}>
      <h1>Search for Books</h1>
      <div className="filter">
        <SearchBar count={count} />
      </div>
    </div>
  )
}


function App() {
  // const dispatch = useDispatch()

  // const handleClick = () => {
  //   dispatch(setBooks())
  // }

  const MainHeader = useCallback(() => (
    <div className="main-image">
      <h1>Search for Books</h1>
      <div className="filter">
        <SearchBar />
      </div>
    </div>
  ), [])

  return (
    <div className="App">
      <SearchComponent />
    </div>


  );
}

export default App;
