import React, { memo, useCallback, useEffect, useRef } from 'react'
import PropTypes from 'prop-types'
import { useDispatch, useSelector } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'

const SearchBar = ({ books, query }) => {
  const dispatch = useDispatch()

	const inputRef = useRef()

	const FoundBooks = useSelector((state) => state.searchBooks.books)

	let inputPlaceHolderDefault = 'Search'

	useEffect(() => {
		if (!FoundBooks) {
			return;
		}

		console.log(FoundBooks)
	}, [FoundBooks])

	const handleSubmit = useCallback((e) => {
		e.preventDefault()
		const inputValue = inputRef.current.value

		if (inputValue !== ''){
			dispatch(getBooks(inputValue))
		} else {
			// let inputPlaceHolderDefault = 'Empty search request'
			console.log('empty')
		}

  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [FoundBooks])

  return (
      <form
				className="formSearchBar"
				onSubmit={handleSubmit}
			>
        <input
					name="input"
					className="searchBar-input"
       		type='text'
        	id='search'
        	value={query}
					ref = {inputRef}
        	placeholder={inputPlaceHolderDefault}
        />
        <button
        	className='search-button'
				>
          <img
            srcSet={searchImage}
            className='search-image' alt=''></img>
        </button>
      </form>
    )
}

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}

export default memo(SearchBar);
