import React, { memo, useCallback, useEffect } from 'react'
import PropTypes from 'prop-types'
import { useDispatch } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'

const SearchBar = ({ books, query }) => {
  const dispatch = useDispatch()

  const handleChange = useCallback((e) => {
      dispatch(getBooks(e.target.value))
  }, [])

  const mappedBooks = !(books == null || undefined) ? books.map((book, index) => {
      console.log(book)
  }) : <h4>No results</h4>

	const onSubmit = () => {
		document.addEventListener("keydown", listener);
	}

	useEffect(() => {
    const listener = event => {
      if (event.code === "Enter" || event.code === "NumpadEnter") {
        console.log("Enter key was pressed. Run your function.");
        event.preventDefault();
        // callMyFunction();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  }, []);

    // TODO: обернуть все в форму и на онсабмит - энтер при фокусе
    return (
        <form className="form"
				onSubmit={onSubmit}>
            <input className="searchBar"
            type='text'
            id='search'
            value={query}
            placeholder={'Search'}
            onChange={handleChange}
            />
            <button
            className='search-button'
            onClick={() => console.log('find-ok')}>
                <img
                srcSet={searchImage}
                className='search-image' alt=''></img>
            </button>
        </form>
    )
}

export default memo(SearchBar);

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}
