import React, { useState } from 'react'
import '../styles/CategorySelector.css'

const DropDownCategorySelector = (props) => {
	const [selected, setSelected] = useState('')
	const options = ['all', 'art', 'biography', 'computers',
'history', 'medical', 'poetry']

	const optionsList = []


return (
    <div>
      <select
			className="category-selector"
			onChange={(e) => {
				const selectedCategory = e.target.value
				setSelected(selectedCategory)
			}}
			options = {options.map((option,index) => <option value={option[index]}>{option}</option>)} />
			{selected}
    </div>
    )
}
export default DropDownCategorySelector;
