import React, { memo, useMemo, useCallback } from 'react'
import PropTypes from 'prop-types'
import { useDispatch } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'


const SearchBar = ({ books, query }) => {
    const dispatch = useDispatch()

    const handleChange = useCallback((e) => {
        dispatch(getBooks(e.target.value))
    }, [])

    const mappedBooks = !(books == null || undefined) ? books.map((book, index) => {
        console.log(book)
    }) : <h4>No results</h4>

    console.log('render');
    // TODO: обернуть в форму и на онсабмит - энтер при фокусе
    return (
        <div className="input-container">
            {/* <form> */}
            <input className="searchBar"
                disabled='true'
                type='text'
                id='search'
                value={query}
                placeholder={'Search'}
                // onClick={Search}
                onChange={handleChange}
            />
            <button className='search-button'
                onClick={console.log('ok')}>
                <img srcSet={searchImage}
                    className='search-image' alt=''></img>
            </button>
            {/* </form> */}
        </div>
    )
}

export default SearchBar;

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}
