import React, { useState } from 'react'
import '../styles/CategorySelector.css'

const OPTIONS_CATEGORY = ['all', 'art', 'biography', 'computers', 'history', 'medical', 'poetry']

const DropdownCategorySelector = (props) => {

	const [selected, setSelected] = useState('')

	const optionsList = OPTIONS_CATEGORY.map(item =>({value: item, label: item, key: item}))

	const optionComponents = optionsList.map((option) => (
		<option key = {option.key} value = {option.value}>{option.label}</option>
	))

	const handleOnChange = (e) => {
		const {selectedCategory} = e.target.value
		setSelected(selectedCategory)
	}

	return (
      <select
			className="category-selector"
			onChange={(e) => {thandleOnChange}}
			>
				{optionComponents}
			</select>
    )
}

export default DropdownCategorySelector;
