import { SET_BOOKS, SET_QUERY, SET_BOOKS_BY_REQUEST } from '../constants/ReduxConstants';
import { apiKey } from '../auth/apiKey'

export const setBooks = (books) => {
  return {
    type: SET_BOOKS,
    payload: books,
  };
};

export const setQuery = (query) => {
  return {
    type: SET_QUERY,
    payload: query,
  };
};

export const setBooksByRequest = () => {
  return {
    type: SET_BOOKS_BY_REQUEST,
    // keys: ,
    // values:
    // как правильней ?
  }
}

export const getBooks = (query) => {
  return async (dispatch) => {
    dispatch(setQuery(query));

     !(query == null) ? fetch("https://www.googleapis.com/books/v1/volumes?q=" + query + '&key' + apiKey)
      .then((response) => {
        if (response.status >= 400) {
          throw new Error("Bad response from server");
        }
        return response.json();
      })
      .then((books) => {
        dispatch(setBooks(books));
      })
      .catch((error) => {
        console.log(error);
      }) : dispatch(setBooks(['oo']))
  };
};
