import React, { useState } from 'react'
import '../styles/CategorySelector.css'

const DropDownCategorySelector = (props) => {
	const [selected, setSelected] = useState('')
	const options = ['all', 'art', 'biography', 'computers',
	'history', 'medical', 'poetry']

	// const FoundBooks = useSelector((state) => state.booksBySearchText.books)

	return (
    <div>
      <select
			className="category-selector"
			onChange={(e) => {
				const selectedCategory = e.target.value
				setSelected(selectedCategory)
			}}
			>
				options
				<option value={options[0]}>{options[0]}</option>
        <option value="art">art</option>
        <option value="biography">biography</option>
        <option value="computers">computers</option>
        <option value="history">history</option>
        <option value="medical">medical</option>
        <option value="poetry">poetry</option>
			</select>
			{selected}
    </div>
    )
}
export default DropDownCategorySelector;
