import React, { memo, useCallback, useRef } from 'react'
import PropTypes from 'prop-types'
import { useDispatch, useSelector } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'
// import {allReducers} from '../../reducers/index'

const SearchBar = ({ books, query }) => {
  const dispatch = useDispatch()

	const FoundBooks = useSelector((state) => state.searchBooks.books)
	const foundBookById = FoundBooks.map((book) => {
		console.log(book)
	})


	const inpurRef = useRef()
	const handleSubmit = useCallback((e) => {
		e.preventDefault()
		const inputValue = inpurRef.current.value
		!(inputValue === '') ?  dispatch(getBooks(inputValue)) : alert('emptyQuery')
		foundBookById
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
      <form
				className="formSearchBar"
				onSubmit={handleSubmit}
			>
        <input
					name="input"
					className="searchBar"
       		type='text'
        	id='search'
        	value={query}
					ref = {inpurRef}
        	placeholder={'Search'}
        />
        <button
        	className='search-button'
				>
          <img
            srcSet={searchImage}
            className='search-image' alt=''></img>
        </button>
      </form>
    )
}

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}

export default memo(SearchBar);
