import React, { memo, useCallback } from 'react'
import PropTypes from 'prop-types'
import { useDispatch } from 'react-redux'
import '../styles/SearchBar.css'
import searchImage from '../../assets/search.png'
import { getBooks } from '../../actions/actions'


const SearchBar = ({ books, query }) => {
    const dispatch = useDispatch()

    const handleChange = useCallback((e) => {
        dispatch(getBooks(e.target.value))
    }, [])

    const mappedBooks = !(books == null || undefined) ? books.map((book, index) => {
        console.log(book)
    }) : <h4>No results</h4>

    // TODO: обернуть все в форму и на онсабмит - энтер при фокусе
    return (
        <form className="form">
        <input className="searchBar"
            type='text'
            id='search'
            value={query}
            placeholder={'Search'}
            onChange={handleChange}
            />
            <button
            className='search-button'
            onClick={() => console.log('find-ok')}>
                <img
                srcSet={searchImage}
                className='search-image' alt=''></img>
            </button>
        </form>
    )
}

export default memo(SearchBar);

SearchBar.propTypes = {
    getBooks: PropTypes.func,
    books: PropTypes.any,
    query: PropTypes.string
}
